import React, {Component} from 'react'
import {Card, Button, Table, Space, notification, Modal, Switch} from 'antd'

import {reqRoleAdd, reqRoleDelete, reqRoleEdit, reqRoleList, reqUserEdit} from '../../../api/api.js'

import {ExclamationCircleOutlined, QuestionCircleOutlined} from "@ant-design/icons"
import RoleModal from '../role/roleModal'

class Role extends Component {

    formRef = React.createRef();

    constructor() {
        super();
        this.state = {
            list: [],
            selectedRowKeys: [],
            pagination: {
                total: 0,
                current: 1,
                pageSize: 20,
                pageSizeOptions: [20, 30, 50, 100],
                showQuickJumper: true,
                showSizeChanger: true
            },
            modalStatus: 'add',
            modalIsVisible: false
        }
    }

    initColumn = () => {
        this.columns = [
            {
                title: '序号',
                render: (text, record, index) => {
                    return (
                        <span>{(this.state.pagination.current - 1) * this.state.pagination.pageSize + (index + 1)}</span>
                    )
                }
            },
            {
                title: '角色名称',
                dataIndex: 'name',
            },
            {
                title: '描述信息',
                dataIndex: 'description',
            },
            {
                title: '排序码',
                dataIndex: 'sort',
            },
            {
                title: '启用状态',
                dataIndex: 'enabled',
                render: (text, record, index) => (
                    <Switch checkedChildren="开启" unCheckedChildren="关闭" checked={text === false ? 0 : 1} defaultChecked
                            onChange={() => this.onChange(text, record)}/>
                )
            },
            {
                title: '创建时间',
                dataIndex: 'gmtCreate',
            }, {
                title: '操作',
                render: (obj) => (
                    <span>
                        <Space>
                            <Button onClick={() => this.showEdit(obj)}>编辑</Button>
                            <Button type='danger' onClick={() => this.deleteRole(obj.id)}>删除</Button>
                        </Space>
                    </span>
                )
            }
        ]
    }

    getRoleList = async (current = 1, pageSize = 20) => {
        const {pagination} = this.state;
        console.log(JSON.stringify(this.formRef))
        // if (null == this.formRef.current) {
        //     this.setState({
        //         list: [],
        //     })
        // } else {
        //     const data = this.formRef.current.getFieldsValue();
        //     console.log(JSON.stringify(this.formRef.current))
        //     data.pageNum = current;
        //     data.pageSize = pageSize;

        // const result = await reqRoleList(data);
        const result = await reqRoleList();
        if (result.code === 200) {
            const data = result.data.records;
            // const data = result.data;
            console.log(JSON.stringify(data))
            this.setState({
                list: data,
                pagination: {
                    ...pagination,
                    total: result.data.total,
                    current: current,
                    pageSize: pageSize
                }
            })
            // }
        }
    }
    //列表搜索 todo

    //table分页
    onTableChange = async (page) => {
        await this.setState({
            pagination: page
        })
        this.getRoleList(page.current, page.pageSize);
    }

    //修改用户状态
    onChange = async (text, record) => {
        Modal.confirm({
            title: '提示',
            okText: '确定',
            cancelText: '取消',
            icon: <ExclamationCircleOutlined/>,
            content: '您确定修改启用状态吗？',
            onOk: async () => {
                record.enabled = !record.enabled
                const result = await reqRoleEdit(record);
                if (result.code === 200) {

                    notification.success({
                        duration: 2,
                        message: '提示',
                        description: result.msg
                    });
                    this.getRoleList();

                } else {
                    notification.error({
                        duration: null,
                        message: '提示',
                        description: result.msg
                    })
                }

            }
        })
    }

    handleCancel = () => {
        this.setState({
            modalIsVisible: false
        })
    }

    //显示添加组件
    showAdd = () => {
        console.log(this.state);
        this.setState({
            modalStatus: 'add',
            modalIsVisible: true
        })
    }

    //显示编辑组件
    showEdit = async (data) => {

        this.setState({
            modalStatus: 'edit',
            modalIsVisible: true
        })

        this.editData = data;
        console.log(JSON.stringify(this.editData) + "：编辑显示")

    }

    //确定回调
    handleOk = (params) => {
        var status = this.state.modalStatus;
        console.log(params, status)
        if ("add" === status) {
            this.addRole(params);
            // this.formRef.current.resetFields()
        } else {
            this.editRole(params);
        }
    }

    //添加方法
    addRole = async (data) => {
        const result = await reqRoleAdd(data);
        if (result.code === 200) {
            this.setState({
                modalIsVisible: false
            })
            notification.success({
                duration: 2,
                message: '提示',
                description: result.msg
            });
            this.getRoleList();
        } else {
            notification.error({
                duration: null,
                message: '提示',
                description: result.msg
            })
        }
    }


    //编辑信息
    editRole = async (data) => {
        const result = await reqRoleEdit(data);
        if (result.code === 200) {
            this.setState({
                modalIsVisible: false
            })
            notification.success({
                duration: 2,
                message: '提示',
                description: result.msg
            });
            this.getRoleList();
        } else {
            notification.error({
                duration: null,
                message: '提示',
                description: result.msg
            })
        }
    }

    //删除信息
    deleteRole = (index) => {

        Modal.confirm({
            title: '提示',
            okText: '确定',
            cancelText: '取消',
            icon: <QuestionCircleOutlined/>,
            content: '您确定删除此条内容吗？',
            onOk: async () => {
                var ids = new Array();
                ids[0] = index;
                const result = await reqRoleDelete(ids);
                if (result.code === 200) {

                    notification.success({
                        duration: 2,
                        message: '提示',
                        description: result.msg
                    });
                    this.getRoleList();

                } else {
                    notification.error({
                        duration: null,
                        message: '提示',
                        description: result.msg
                    })
                }

            }
        })
    }

    deleteBatch = () => {
        Modal.confirm({
            title: '提示',
            okText: '确定',
            cancelText: '取消',
            icon: <QuestionCircleOutlined/>,
            content: `您确定删除${this.state.selectedRowKeys.length}项吗？`,
            onOk: async () => {

                const ids = this.state.selectedRowKeys;

                const result = await reqRoleDelete(ids);
                if (result.code === 200) {
                    notification.success({
                        duration: 2,
                        message: '提示',
                        description: result.msg
                    });
                    this.setState({
                        selectedRowKeys: []
                    });
                    this.getRoleList();

                } else {
                    notification.error({
                        duration: null,
                        message: '提示',
                        description: result.msg
                    })
                }

            }
        })
    }

    resetSearch = async () => {
        this.formRef.current.resetFields()
        const result = await reqRoleList({pageNum: 1, pageSize: 10});
        if (result.code === 200) {
            const data = result.data;
            // const data = result.data.records;
            this.setState({list: data})
        }
    }

    componentWillMount() {
        this.initColumn();
    }

    componentDidMount() {
        this.getRoleList();
    }

    render() {

        // const {roleList, showStatus} = this.state;
        // const title = (
        //     <span>
        //         <Button type="primary" onClick={() => this.toggleVisible(1)}>新增</Button>
        //     </span>
        // )

        const editData = this.editData;
        const {list, selectedRowKeys, pagination, modalIsVisible, modalStatus} = this.state;


        const rowSelection = {
            selectedRowKeys: selectedRowKeys,
            onChange: (selectedRowKeys) => this.setState({selectedRowKeys})
        }

        return (
            <Card>
                <Table
                    rowSelection={rowSelection}
                    columns={this.columns}
                    rowKey="id"
                    dataSource={list}
                    pagination={pagination}
                    onChange={this.onTableChange}
                    style={{marginTop: 10}}/>
                {
                    modalIsVisible &&
                    <RoleModal visible={modalIsVisible} modalStatus={modalStatus} currentItem={editData}
                               onCancel={this.handleCancel} handleOk={this.handleOk}/>
                }
            </Card>
        );
    }
}

export default Role;
