import React, {Component} from 'react';
import {Card, Button, Table, Space} from 'antd'

import {reqRoleList, reqUserList} from '../../../api/api.js'

import RoleAdd from '../role/roleAdd'

class Role extends Component {

    constructor() {
        super();
        this.state = {
            list: [],
            selectedRowKeys: [],
            pagination: {
                total: 0,
                current: 1,
                pageSize: 20,
                pageSizeOptions: [20, 30, 50, 100],
                showQuickJumper: true,
                showSizeChanger: true
            },
            modalStatus: 'add',
            modalIsVisible: false
        }
    }

    initColumn = () => {
        this.columns = [
            {
                title: '序号',
                render: (text, record, index) => {
                    return (
                        <span>{(this.state.pagination.current - 1) * this.state.pagination.pageSize + (index + 1)}</span>
                    )
                }
            },
            {
                title: '角色名称',
                dataIndex: 'name',
            },
            {
                title: '描述信息',
                dataIndex: 'description',
            },
            {
                title: '排序码',
                dataIndex: 'sort',
            },
            {
                title: '启用状态',
                dataIndex: 'enabled',
            },
            {
                title: '创建时间',
                dataIndex: 'gmtCreate',
            }, {
                title: '操作',
                render: (obj) => (
                    <span>
                        <Space>
                            <Button onClick={() => this.showEdit(obj)}>编辑</Button>
                            <Button type='danger' onClick={() => this.deleteUser(obj.id)}>删除</Button>
                        </Space>
                    </span>
                )
            }
        ]
    }

    getRoleList = async (current = 1, pageSize = 20) => {
        const {pagination} = this.state;

        const data = this.formRef.current.getFieldsValue();
        data.pageNum = current;
        data.pageSize = pageSize;

        const result = await reqRoleList(data);
        if (result.code === 200) {
            // const data = result.data.records;
            const data = result.data;
            console.log(JSON.stringify(data))
            this.setState({
                list: data,
                pagination: {
                    ...pagination,
                    total: result.data.total,
                    current: current,
                    pageSize: pageSize
                }
            })
        }
    }

    // getRoleList = async () => {
    //     const result = await reqRoleList();
    //     if (result.code === 200) {
    //         const data = result.data.records;
    //         this.setState({roleList: data})
    //     }
    // }
    //列表搜索 todo

    //table分页
    onTableChange = async (page) => {
        await this.setState({
            pagination: page
        })
        this.getUserList(page.current, page.pageSize);
    }

    handleCancel = () => {
        this.setState({
            modalIsVisible: false
        })
    }

    //显示添加组件
    showAdd = () => {
        console.log(this.state);
        this.setState({
            modalStatus: 'add',
            modalIsVisible: true
        })
    }

    //显示编辑组件
    showEdit = async (data) => {

        this.setState({
            modalStatus: 'edit',
            modalIsVisible: true
        })

        this.editData = data;
        console.log(JSON.stringify(this.editData) + "：编辑显示")

    }

    //确定回调
    handleOk = (params) => {
        var status = this.state.modalStatus;
        console.log(params, status)
        if ("add" === status) {
            this.addUser(params);
            // this.formRef.current.resetFields()
        } else {
            this.editUser(params);
        }
    }

    resetSearch = async () => {
        this.formRef.current.resetFields()
        const result = await reqRoleList({pageNum: 1, pageSize: 10});
        if (result.code === 200) {
            const data = result.data;
            // const data = result.data.records;
            this.setState({list: data})
        }
    }

    componentWillMount() {
        this.initColumn();
    }

    componentDidMount() {
        this.getRoleList();
    }

    render() {

        // const {roleList, showStatus} = this.state;
        // const title = (
        //     <span>
        //         <Button type="primary" onClick={() => this.toggleVisible(1)}>新增</Button>
        //     </span>
        // )

        const editData = this.editData;
        const {list, selectedRowKeys, pagination, modalIsVisible, modalStatus} = this.state;


        const rowSelection = {
            selectedRowKeys: selectedRowKeys,
            onChange: (selectedRowKeys) => this.setState({selectedRowKeys})
        }

        return (
            <Card >
                {/*<Table*/}
                {/*    rowKey="id"*/}
                {/*    rowSelection={{type: "checkout"}}*/}
                {/*    columns={this.columns}*/}
                {/*    dataSource={roleList}/>*/}
                <Table
                    rowSelection={rowSelection}
                    columns={this.columns}
                    rowKey="id"
                    dataSource={list}
                    pagination={pagination}
                    onChange={this.onTableChange}
                    style={{marginTop: 10}}/>
                {/*<RoleAdd visible={showStatus} toggleVisible={this.toggleVisible} onRefresh={this.getRoleList}/>*/}
            </Card>
        );
    }
}

export default Role;
